import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { selectIsLogin } from '../../store/app.selector';
import { take } from 'rxjs/operators';
import * as MyActions from '../../store/app.actions';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  isLogin:boolean =true


  constructor(private store:Store,private authService: AuthService,,private router:Router){

  }
ngOnInit(){
  this.isAuthenticated = this.authService.isAuthenticated();
    const token = this.authService.getAuthToken()
    if(token){
      this.store.dispatch(MyActions.login());
      this.router.navigate(['/']);
    }
  this.store.select(selectIsLogin).subscribe((isLogin) => {
    this.isLogin = isLogin;
  });
}

  getislogin(){
    this.store
    .select(selectIsLogin)
    .pipe(take(1)) // Use 'take(1)' to automatically unsubscribe after the first emission
    .subscribe((isLogin) => {
      this.isLogin = isLogin;

    });
  }


}
