import { Component, OnInit } from '@angular/core';
import { Store } from '@ngrx/store';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { catchError, retry } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-cart-info',
  templateUrl: './cart-info.component.html',
  styleUrls: ['./cart-info.component.css'],
})
export class CartInfoComponent implements OnInit {
  cart: any;
  responseData: any;
  constructor(
    private store: Store,
    private authService: AuthService,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit() {
    const token = this.authService.getAuthToken();
    if (!token) {
      this.router.navigate(['/login']);
    }

    const url = 'http://localhost:3000/cart';

    this.http
      .get(url)
      .pipe(
        catchError((error) => {
          console.error('Error sending POST request', error);
          throw error;
        })
      )
      .subscribe((response) => {
        console.log('POST request successful', response);
        this.responseData = response;
        this.cart = this.responseData.items;
        const product = this.cart.product;
        console.log(product);
      });
  }

  delete(id) {
    console.log(id);
    this.cart = this.cart.filter((item) => item.product._id != id);
    const PostData = {
      itemId: id,
    };
    console.log(PostData);
    const url = 'http://localhost:3000/cart/products/delete';

    this.http
      .post(url, PostData)
      .pipe(
        catchError((error) => {
          console.error('Error sending POST request', error);
          throw error;
        })
      )
      .subscribe((response) => {
        console.log('POST request successful', response);
      });
  }
}
<div class="container mycart"><h1>Your Cart</h1></div>

<div *ngFor="let item of cart" class="container mycart">
  <div class="card mb-3">
    <div class="row g-0">
      <div class="col-md-4">
        <img
          [src]="item.product.image_url"
          alt="{{ item.product.title }}"
          height="150px"
          width="'200px"
        />
      </div>
      <div class="col-md-8">
        <div class="card-body">
          <h3 class="card-title">{{ item.product.title }}</h3>
          <div class="row">
            <div class="col-md-6">
              <p class="card-text">
                <strong>Price:</strong> ${{ item.product.price }}
              </p>
            </div>
            <div class="col-md-6">
              <p class="card-text">
                <strong>Quantity:</strong> {{ item.quantity }}
              </p>
            </div>
          </div>
          <p class="card-text">
            <strong>Total:</strong> ${{ item.quantity * item.product.price }}
          </p>
          <div class="remove">
            <input type="hidden" value="{{ item.id }}" name="itemId" />
            <button class="btn btn-danger" (click)="delete(item.product._id)">
              X
            </button>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>

<div class="container mycart">
  <button class="btn btn-primary" *ngIf="cart.length >= 1">CheckOut</button>
</div>
