import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { HttpClient } from '@angular/common/http';
import { catchError, retry } from 'rxjs/operators';
import { Store } from '@ngrx/store';
import { selectIsLogin } from '../../store/app.selector';
import { take } from 'rxjs/operators';
import * as MyActions from '../../store/app.actions';
import { AuthService } from '../auth.service';
import { ChangeDetectionStrategy } from '@angular/core';
import { Location } from '@angular/common';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
  changeDetection: ChangeDetectionStrategy.Default
})
export class LoginComponent implements OnInit {
  responseData:any
  loginForm: FormGroup;
  isLogin: boolean | undefined;
  isAuthenticated: boolean = false;
  constructor(private fb: FormBuilder, private http: HttpClient,private store: Store,private authService: AuthService,private location:Location,private router:Router) { 


  }

  ngOnInit() {


    this.isAuthenticated = this.authService.isAuthenticated();
    const token = this.authService.getAuthToken()
    if(token){
      this.store.dispatch(MyActions.login());
      this.router.navigate(['/']);
    }
    // console.log(token)

    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });

    this.store.select(selectIsLogin).subscribe((isLogin) => {
      this.isLogin = isLogin;
    });


  }

  onSubmit() {

    if (this.loginForm.valid) {

      console.log(this.loginForm.value);

      const postData = {
         ...this.loginForm.value
      };

      const url = 'http://localhost:3000/users/login'; 

      this.http.post(url, postData)
      .pipe(
        catchError((error) => {

          console.error('Error sending POST request', error);
          throw error;
        })
      )
      .subscribe((response) => {
        console.log('POST request successful', response);

        this.responseData =response
        if(this.responseData){
        const user = this.responseData.user
        const authToken = this.responseData.token  
        this.authService.setAuthData(user,authToken);
        this.isAuthenticated = true;
        this.store.dispatch(MyActions.login());
        this.router.navigate(['/']);

        }




      });
    }
  }
}
