import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, BehaviorSubject } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private baseUrl = 'http://localhost:3000/products/allProducts';
  private products$: BehaviorSubject<any[]> = new BehaviorSubject<any[]>([]);
  private product$: BehaviorSubject<any | null> = new BehaviorSubject<any | null>(null);

  constructor(private http: HttpClient) {
    this.fetchProducts();
  }



  fetchProducts(): void {
    this.http.get<any[]>(this.baseUrl).pipe(
      catchError(error=>{
        throw error
      }
        )
    ).subscribe(data => {
      this.products$.next(data);
    });
  }

  getProducts(): Observable<any[]> {
    return this.products$.asObservable();
  }

