import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, of, throwError } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private products$: Observable<any[]>;
  products:any;
  private product$:Observable<any[]>;
  private baseUrl = 'http://localhost:3000/products/allProducts';

  constructor(private http: HttpClient) { }

  fetchProducts(): Observable<any[]> {
    this.products$ = this.http.get<any[]>(this.baseUrl).pipe(
      catchError((error) => {
        console.error('Error sending get request', error);
        throw error;
      }),
      tap((data) => {

        this.products$ = of(data);
      })
    );

    return this.products$;
  }

  getStoredProducts(): Observable<any[]> {
    if (this.products$) {
      return this.products$;
    } else {

      return this.fetchProducts();
    }
  }


  fetchProduct(id:any):Observable<any[]>{
    this.product$ = this.getStoredProducts().pipe(
      tap(products => {
        const product = products.find(p => p._id === id);
        console.log(product)
        this.product$ = of(product)

      })
    );
    return this.product$
  }

  getProduct(id:any):Observable<any[]>{


      return this.fetchProduct(id);

  }
}
