import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable, throwError } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class ProductService {
  private products$: Observable<any[]>;
  private baseUrl = 'http://localhost:3000/products/allProducts';

  constructor(private http: HttpClient) { }

  // Method to fetch products and store them in an observable property
  fetchProducts(): Observable<any[]> {
    this.products$ = this.http.get<any[]>(this.baseUrl).pipe(
      catchError((error) => {
        console.error('Error sending get request', error);
        thr;
      }),
      tap((data) => {
        // Store the fetched products in the products$ observable property
        this.products$ = of(data);
      })
    );

    return this.products$;
  }

  // Method to get the stored products without making another HTTP request
  getStoredProducts(): Observable<any[]> {
    if (this.products$) {
      return this.products$;
    } else {
      // If products$ is not set, fetch the products from the API
      return this.fetchProducts();
    }
  }
}
